// Parse Telegram message
const message = $input.item.json.message;
const fullText = message.text || '';

// Extract command (remove /chucky prefix)
const command = fullText.replace(/^\/chucky\s*/i, '').trim();

// Get user information
const userId = message.from.id;
const username = message.from.username || message.from.first_name || 'Unknown';
const chatId = message.chat.id;

// Security: Whitelist check - YOUR TELEGRAM USER ID
const ALLOWED_USER_IDS = [
  8445149012  // Your actual Telegram user ID from .env
];

if (!ALLOWED_USER_IDS.includes(userId)) {
  throw new Error(`Unauthorized user: ${username} (${userId})`);
}

// Validate command is not empty
if (!command || command.length === 0) {
  return {
    json: {
      chatId: chatId,
      error: true,
      errorMessage: 'Please provide a command after /chucky\n\nExample:\n/chucky create a webhook node'
    }
  };
}

// Security: Sanitize command (prevent injection)
const sanitized = command
  .replace(/[;&|`$()]/g, '')  // Remove shell special characters
  .replace(/\.\.\//g, '')     // Remove directory traversal
  .trim();

if (sanitized !== command) {
  return {
    json: {
      chatId: chatId,
      error: true,
      errorMessage: '‚ö†Ô∏è Command contains potentially unsafe characters and was blocked.'
    }
  };
}

// Rate limiting check
const now = Date.now();
const RATE_LIMIT_WINDOW = 5 * 60 * 1000; // 5 minutes
const MAX_COMMANDS = 10;

if (!$execution.customData.has('rateLimits')) {
  $execution.customData.set('rateLimits', {});
}

const rateLimits = $execution.customData.get('rateLimits');
const userKey = `user_${userId}`;

if (!rateLimits[userKey]) {
  rateLimits[userKey] = {
    commands: [],
    windowStart: now
  };
}

const userLimit = rateLimits[userKey];

// Clean old commands outside window
userLimit.commands = userLimit.commands.filter(t => t > now - RATE_LIMIT_WINDOW);

// Check if over limit
if (userLimit.commands.length >= MAX_COMMANDS) {
  const oldestCommand = Math.min(...userLimit.commands);
  const waitTime = Math.ceil((RATE_LIMIT_WINDOW - (now - oldestCommand)) / 1000);

  return {
    json: {
      chatId: chatId,
      error: true,
      errorMessage: `‚ö†Ô∏è Rate limit exceeded. Please wait ${waitTime} seconds before sending more commands.\n\nLimit: ${MAX_COMMANDS} commands per ${RATE_LIMIT_WINDOW / 60000} minutes.`
    }
  };
}

// Add current command to rate limit tracking
userLimit.commands.push(now);
$execution.customData.set('rateLimits', rateLimits);

// Return parsed and validated data
return {
  json: {
    chatId: chatId,
    userId: userId,
    username: username,
    command: sanitized,
    originalMessage: fullText,
    timestamp: new Date().toISOString(),
    error: false
  }
};

// Get command output
const cmdResult = $input.item.json;
const stdout = cmdResult.stdout || '';
const stderr = cmdResult.stderr || '';
const exitCode = cmdResult.exitCode || 0;

// Get chat ID from earlier in workflow
const chatId = $('Parse & Validate Command').item.json.chatId;
const command = $('Parse & Validate Command').item.json.command;

// Determine if successful
const isSuccess = exitCode === 0 && stdout.length > 0;

// Format output
let output = '';

if (isSuccess) {
  output = stdout;
} else if (stderr) {
  output = `‚ùå Error:\n\n${stderr}`;
} else {
  output = `‚ö†Ô∏è Command completed but returned no output.\n\nExit code: ${exitCode}`;
}

// Truncate if too long (Telegram limit is 4096 characters)
const MAX_LENGTH = 4000;
let truncated = false;

if (output.length > MAX_LENGTH) {
  output = output.substring(0, MAX_LENGTH);
  truncated = true;
}

// Format final message
let message = '';

if (isSuccess) {
  message = `‚úÖ **Command Completed**\n\n`;
  message += `üìù Request: \`${command}\`\n\n`;
  message += `üì§ Result:\n\`\`\`\n${output}\n\`\`\``;

  if (truncated) {
    message += `\n\n‚ö†Ô∏è Output was truncated (exceeded ${MAX_LENGTH} characters)`;
  }
} else {
  message = `‚ùå **Command Failed**\n\n`;
  message += `üìù Request: \`${command}\`\n\n`;
  message += `${output}`;
}

// Add timestamp
const duration = Date.now() - new Date($('Parse & Validate Command').item.json.timestamp).getTime();
message += `\n\n‚è± Execution time: ${(duration / 1000).toFixed(1)}s`;

// Log to console for debugging
console.log(`Chucky Remote: ${isSuccess ? 'SUCCESS' : 'FAILED'} - User: ${$('Parse & Validate Command').item.json.username}`);

return {
  json: {
    chatId: chatId,
    message: message,
    success: isSuccess,
    executionTimeMs: duration,
    exitCode: exitCode,
    // For audit logging
    userId: $('Parse & Validate Command').item.json.userId,
    username: $('Parse & Validate Command').item.json.username,
    command: command,
    timestamp: $('Parse & Validate Command').item.json.timestamp
  }
};
